import React, { useState, useEffect } from 'react'
import { useHistory } from 'react-router-dom'
import axios from 'axios'

export default function Home(){
  const history = useHistory()
  const [name, SetName] = useState("")
  const [email, SetEmail] = useState("")
  const [password, SetPassword] = useState("")
  const [dataBackend, setDataBackend] = useState("")
  const [test, setTest] = useState(false)
  const [userName, setUserName] = useState('')

  axios.defaults.withCredentials = true

  useEffect(() => {
      const ifLoggedCheck = async () => {
        try {
          await axios.get('http://localhost:4000').then(res => {
            console.log(res)
            if(res.data.msg === 'logged in'){
              setTest(true)
              setUserName(res.data.name)

            }else{
              setTest(false)
              setUserName(res.data.name)
            }
        })
        } catch (error) {
          console.log(error)
        }}
        ifLoggedCheck() 
  })
  const handleSubmit = async (e) => {
    e.preventDefault()
    try {
      await axios.post('http://localhost:4000',{
      name,
      email,
      password
    }).then(res => {
      if(res.data.msg === 'this email is already used!'){
        alert('This email is already used!')
        return
      }
      setDataBackend(res.data.msg)
      setUserName(res.data.userName)
      console.log(dataBackend)
    })
    } catch (error) {
      console.log(error)
    }



  }
  const handleClick = (e) => {
    e.preventDefault()
    history.push('/login')
  }

    return (
      test ? (
        <div className="home-page">
          <div className="loggedIn-home-page">
            Welcome, {userName}!
          </div>
        </div>
      )
       :
     ( <div className="home-page-register">
          <form className="form">
             <label>Name</label>
             <input type="text" placeholder="name" value={name} onChange={(e) => SetName(e.target.value)}/>
             <label>Email</label>
             <input type="text" placeholder="john@gmail.com" value={email} onChange={(e)=> SetEmail(e.target.value)}/>
             <label>Password</label>
             <input type="password" placeholder="Password" value={password} onChange={(e) => SetPassword(e.target.value)}/>
             <button onClick={(e) => handleSubmit(e)} >Register</button>
          </form>
          <p className="login-p">Alread have an account<button onClick={(e) => handleClick(e)} className="login-btn">login</button></p>
      </div>
     )
    )
}
